solution:
// HTML structure:
// <div id="title">Original Title</div>
// <p class="description">Original description</p>
// <button onclick="changeContent()">Change Content</button>

function changeContent() {
  // Select the title element and change its text
  const title = document.getElementById('title');
  title.textContent = 'New Title';

  // Select the description element and change its text
  const description = document.querySelector('.description');
  description.textContent = 'New description';
}

